(54, 'wc-admin-learn-more-about-variable-products', 'info', 'en_US', 'Learn more about variable products', 'Variable products are a powerful product type that lets you offer a set of variations on a product, with control over prices, stock, image and more for each variation. They can be used for a product like a shirt, where you can offer a large, medium and small and in different colors.', '{}', 'unactioned', 'woocommerce-admin', '2021-10-11 05:27:33', NULL, 0, 'plain', '', 0, 'info'),
(55, 'bbpos-chipper-launch', 'marketing', 'en_US', 'Ready to grow your business beyond online?', 'Collect payment in person for orders placed online using WooCommerce In-Person Payments. The BBPOS Chipper card reader can process payments securely, and keep everything synced in your WooCommerce Payments dashboard – no matter where you are.', '{}', 'pending', 'woocommerce.com', '2021-10-15 10:11:58', NULL, 0, 'plain', '', 0, 'info'),
